mapper.select:
select sum(t2.goods_num*t2.single_pi_price)from daifa_after_receive_expres_stock t1,daifa_after_sale_sub t2
where t1.relevance_status=1 and t1.df_order_id=t2.df_order_id
and t2.after_status=4
and t2.seller_id= ${sellerId}
AND date_format(t1.create_time, "%Y%m%d")=${day}

select sum(goods_num*single_pi_price) from daifa_ggoods_tasks t1
where seller_id= ${sellerId}
and t1.allocat_date = ${day}

SELECT t1.user_Name `name`, t1.daifa_worker_id workerId, CONCAT(t2.num,"/",t2.total_num) num, t2.totalPrice, t2.takePrice FROM daifa_worker t1, ( SELECT daifa_worker_id, sum(if(take_goods_status=1,goods_num,0)) num, sum(goods_num) total_num, sum(if(take_goods_status=1,goods_num*single_pi_price,0)) takePrice, sum(goods_num*single_pi_price) totalPrice FROM daifa_ggoods WHERE create_date >= ${day}
and create_date <= ${endDay}
AND seller_id=${sellerId} GROUP BY daifa_worker_id ) t2 WHERE t1.daifa_worker_id = t2.daifa_worker_id

SELECT
    count(*)
FROM
    daifa_order t1,
    daifa_trade t2
WHERE
    (
        date_format(
            t1.return_goods_finish_time,
            "%Y%m%d"
        ) = ${day}
        OR date_format(
            t1.refund_finish_time,
            "%Y%m%d"
        ) = ${day}
    )
AND t1.df_trade_id = t2.df_trade_id
AND t2.seller_id = ${sellerId}
