mapper.select:
SELECT v.stock_id stockId, v.df_order_id dfOrderId FROM ( SELECT t.* FROM ( SELECT t1.stock_id, t1.df_order_id, t1.start_in_time, t2.stock_record_id, t2.in_out_type, t2.in_time, t2.out_time, t2.return_time FROM daifa_stock t1 LEFT JOIN daifa_stock_record t2 ON t1.stock_id = t2.stock_id
WHERE t2.daifa_worker_id = #{workerId}
ORDER BY t1.stock_id DESC, t2.stock_record_id DESC ) t GROUP BY t.stock_id ) v WHERE v.in_out_type = ${inOutType}

select count(*) from daifa_stock t1
LEFT JOIN daifa_stock_record t2
on t1.stock_id=t2.stock_id
where t1.seller_id=${sellerId}
and t2.in_out_type=${inOutType}

    and DATE_FORMAT(t2.in_time,"%Y%m%d")=${day}


    and DATE_FORMAT(t2.out_time,"%Y%m%d")=${day}


    and DATE_FORMAT(t2.return_time,"%Y%m%d")=${day}

select t2.stock_record_id from daifa_stock t1
LEFT JOIN daifa_stock_record t2
on t1.stock_id=t2.stock_id
where t1.seller_id=${sellerId}
and t2.in_out_type=${inOutType}


      and DATE_FORMAT(t2.in_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.in_time,"%Y%m%d") >= ${endDay}




        and DATE_FORMAT(t2.out_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.out_time,"%Y%m%d") >= ${endDay}




        and DATE_FORMAT(t2.return_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.return_time,"%Y%m%d") >= ${endDay}


limit #{start},#{end}

select count(*) from daifa_stock t1
LEFT JOIN daifa_stock_record t2
on t1.stock_id=t2.stock_id
where t1.seller_id=${sellerId}
and t2.in_out_type=${inOutType}


        and DATE_FORMAT(t2.in_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.in_time,"%Y%m%d") >= ${endDay}




        and DATE_FORMAT(t2.out_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.out_time,"%Y%m%d") >= ${endDay}




        and DATE_FORMAT(t2.return_time,"%Y%m%d") <= ${startDay}


        and DATE_FORMAT(t2.return_time,"%Y%m%d") >= ${endDay}
