SELECT f.dws_id, f.df_trade_id, f.send_status, DATE_FORMAT(f.create_time,'%Y-%m-%d %H:%i:%s')create_time, f.buyer_ww, f.buyer_qq, f.buyer_telephone, f.receiver_name, f.receiver_phone, f.buyer_remark, f.money, f.express_fee, f.express_name, f.express_code, f.trade_discount_fee, f.services_fee, f.receiver_address, f.daifa_type=2 is_tb_order, d.df_order_id, d.pic_path, d.title, d.prop_str, d.goods_code, d.single_price, d.single_pi_price, d.goods_num, d.take_goods_status, d.refund_status, d.store_goods_code FROM ( SELECT e.*,c.trade_discount_fee,c.services_fee,c.daifa_type FROM daifa_wait_send e, ( SELECT v.DWS_ID, v.df_trade_id, t.trade_discount_fee, t.services_fee, t.daifa_type FROM (select t1.df_trade_id,t1.dws_id FROM daifa_wait_send t1 JOIN (select dws_id from daifa_wait_send_order where refund_status=0 GROUP BY df_trade_id) t3 ON t1.dws_id=t3.dws_id LEFT JOIN ( SELECT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.take_goods_status != 1 AND b.refund_status = 0 GROUP BY df_trade_id ) t2 ON t1.dws_id = t2.dws_id WHERE SEND_STATUS = 1 AND SELLER_ID = ${dfSellerId} AND t1.order_display = 1
AND t2.df_trade_id IS NULL
AND t2.df_trade_id IS NOT NULL
AND t1.df_trade_id like("%${lastTradeId}")
AND t1.receiver_phone = #{phone}
AND t1.buyer_id = ${buyerId}
) v JOIN daifa_trade t ON v.df_trade_id=t.df_trade_id WHERE t.df_trade_id>0
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
ORDER BY v.dws_id DESC limit ${start},${end} ) c WHERE c.DWS_ID = e.DWS_ID ) as f LEFT JOIN daifa_wait_send_order as d ON f.DWS_ID = d.DWS_ID

SELECT count(*) FROM daifa_wait_send e, ( SELECT v.DWS_ID, v.df_trade_id, t.trade_discount_fee, t.services_fee FROM (select t1.df_trade_id,t1.dws_id from daifa_wait_send t1 JOIN (select dws_id from daifa_wait_send_order where refund_status=0 GROUP BY df_trade_id) t3 ON t1.dws_id=t3.dws_id LEFT JOIN ( SELECT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.take_goods_status != 1 AND b.refund_status = 0 GROUP BY df_trade_id ) t2 ON t1.dws_id = t2.dws_id WHERE SEND_STATUS = 1 AND SELLER_ID = ${dfSellerId} AND t1.order_display = 1
AND t2.df_trade_id IS NULL
AND t2.df_trade_id IS NOT NULL
AND t1.df_trade_id like("%${lastTradeId}")
AND t1.receiver_phone = #{phone}
AND t1.buyer_id = ${buyerId}
) v JOIN daifa_trade t ON v.df_trade_id=t.df_trade_id WHERE t.df_trade_id>0
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
ORDER BY v.dws_id DESC ) c WHERE c.DWS_ID = e.DWS_ID
