mapper.select:
SELECT f.dws_id, f.df_trade_id, f.send_status, DATE_FORMAT( f.create_time, '%Y-%m-%d %H:%i:%s' ) create_time, f.buyer_ww, f.buyer_qq, f.buyer_telephone, f.receiver_name, f.receiver_phone, f.buyer_remark, f.money, f.express_fee, f.express_name, f.express_code, t.trade_discount_fee, t.services_fee, f.receiver_address, t.daifa_type = 2 is_tb_order, t.bar_code_key, d.df_order_id, d.pic_path, d.title, d.prop_str, d.goods_code, d.single_price, d.single_pi_price, d.goods_num, d.take_goods_status, d.refund_status, d.store_goods_code FROM daifa_wait_send f, daifa_wait_send_order d, ( SELECT DISTINCT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b
LEFT JOIN ( SELECT DISTINCT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.take_goods_status != 1 AND b.refund_status = 0 ) t2 ON b.dws_id = t2.dws_id
WHERE a.dws_id = b.dws_id AND a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.refund_status = 0
AND t2.df_trade_id IS NULL
AND b.take_goods_status != 1
AND a.df_trade_id like("%${lastTradeId}")
AND a.receiver_phone = #{phone}
AND a.buyer_id = ${buyerId}
) v, daifa_trade t WHERE f.dws_id = d.dws_id AND f.dws_id = v.dws_id AND v.df_trade_id = t.df_trade_id AND t.df_trade_id > 0
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
ORDER BY f.dws_id DESC limit ${start},${end}

SELECT count(*) FROM( SELECT DISTINCT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b
LEFT JOIN ( SELECT DISTINCT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.take_goods_status != 1 AND b.refund_status = 0 ) t2 ON b.dws_id = t2.dws_id
WHERE a.dws_id = b.dws_id AND a.SELLER_ID = ${dfSellerId} AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.DWS_ID = b.DWS_ID AND b.refund_status = 0
AND t2.df_trade_id IS NULL
AND b.take_goods_status != 1
AND a.df_trade_id like("%${lastTradeId}")
AND a.receiver_phone = #{phone}
AND a.buyer_id = ${buyerId}
) v, daifa_trade t WHERE v.df_trade_id = t.df_trade_id AND t.df_trade_id > 0
AND t.create_time > #{startTime}
AND t.create_time < #{endTime}
