mapper.select:
SELECT t1.df_trade_id orderId, t1.dws_id dwsId FROM( SELECT t1.df_trade_id, t1.dws_id, t1.receiver_phone, t1.buyer_id FROM daifa_wait_send t1, daifa_wait_send_order t2 WHERE t1.df_trade_id = t2.df_trade_id
AND t1.df_trade_id like("%${lastTradeId}") AND t2.df_trade_id like("%${lastTradeId}")
AND t1.df_trade_id >=#{stId} AND t2.df_trade_id >=#{stId}
AND t1.df_trade_id <=#{etId} AND t2.df_trade_id <=#{etId}
and t2.refund_status = 0 and t1.SEND_STATUS = 1 AND t1.order_display = 1 AND t1.seller_id = ${dfSellerId} GROUP BY t1.df_trade_id ) t1 LEFT JOIN ( SELECT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId}
AND a.df_trade_id like("%${lastTradeId}") AND b.df_trade_id like("%${lastTradeId}")
AND a.df_trade_id >=#{stId} AND b.df_trade_id >=#{stId}
AND a.df_trade_id <=#{etId} AND b.df_trade_id <=#{etId}
AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.df_trade_id = b.df_trade_id AND b.take_goods_status != 1 AND b.refund_status = 0 GROUP BY df_trade_id ) t2 ON t1.dws_id = t2.dws_id WHERE 1=1
AND t2.df_trade_id IS NULL
AND t2.df_trade_id IS NOT NULL
AND t1.receiver_phone = #{phone}
AND t1.buyer_id = ${buyerId}
limit ${start},${end}

SELECT count(*) FROM( SELECT t1.df_trade_id, t1.dws_id, t1.receiver_phone, t1.buyer_id FROM daifa_wait_send t1, daifa_wait_send_order t2 WHERE t1.df_trade_id = t2.df_trade_id
AND t1.df_trade_id like("%${lastTradeId}") AND t2.df_trade_id like("%${lastTradeId}")
AND t1.df_trade_id >=#{stId} AND t2.df_trade_id >=#{stId}
AND t1.df_trade_id <=#{etId} AND t2.df_trade_id <=#{etId}
and t2.refund_status = 0 and t1.SEND_STATUS = 1 AND t1.order_display = 1 AND t1.seller_id = ${dfSellerId} GROUP BY t1.df_trade_id ) t1 LEFT JOIN ( SELECT a.df_trade_id, a.dws_id FROM daifa_wait_send a, daifa_wait_send_order b WHERE a.SELLER_ID = ${dfSellerId}
AND a.df_trade_id like("%${lastTradeId}") AND b.df_trade_id like("%${lastTradeId}")
AND a.df_trade_id >=#{stId} AND b.df_trade_id >=#{stId}
AND a.df_trade_id <=#{etId} AND b.df_trade_id <=#{etId}
AND a.SEND_STATUS = 1 AND a.order_display = 1 AND a.df_trade_id = b.df_trade_id AND b.take_goods_status != 1 AND b.refund_status = 0 GROUP BY df_trade_id ) t2 ON t1.dws_id = t2.dws_id WHERE 1=1
AND t2.df_trade_id IS NULL
AND t2.df_trade_id IS NOT NULL
AND t1.receiver_phone = #{phone}
AND t1.buyer_id = ${buyerId}
