mapper.select:
SELECT r.*, s.sub_user_name as user_nick,u.login_phone,substring(r.ward,2,1) as s FROM active_draw_record r, member_user u, member_user_sub s WHERE r.user_id = u.user_id and u.user_id = s.user_id and s.account_type = 3
AND r.pem_id = #{pemId}
AND r.user_id = #{userId}
AND r.ward = #{ward}
AND r.ward != 'A1' AND r.ward != 'B1' AND r.draw_status = 3 AND draw_code is not null ORDER BY r.pem_id DESC,s DESC LIMIT 80
ORDER BY r.draw_status DESC,r.create_time ASC limit #{startRows},#{endRows}

SELECT count(r.id) FROM active_draw_record r, member_user u, member_user_sub s WHERE r.user_id = u.user_id and u.user_id = s.user_id and s.account_type = 3
AND r.pem_id = #{pemId}
AND r.user_id = #{userId}
AND r.ward = #{ward}
