mapper.select:
SELECT t1.goods_id, t1.goods_no, t1.pic_url FROM ( SELECT goods_id FROM shigu_goods_tiny_${webSite} WHERE store_id = #{shopId} AND goods_status = 0 AND is_closed = 0 ) t1 LEFT JOIN ( SELECT goods_id FROM shigu_gys_category_goods WHERE s_id = #{shopId} AND gcg_status = 1
AND gc_id = #{cateId}
)t2 on t1.goods_id=t2.goods_id where
t2.goods_id is NOT null
t2.goods_id is null
LIMIT #{pageNo}, #{pageSize}

SELECT COUNT(t1.goods_id) FROM ( SELECT goods_id FROM shigu_goods_tiny_${webSite} WHERE store_id = #{shopId} AND goods_status = 0 AND is_closed = 0 ) t1 LEFT JOIN ( SELECT goods_id FROM shigu_gys_category_goods WHERE s_id = #{shopId} AND gcg_status = 1
AND gc_id = #{cateId}
)t2 on t1.goods_id=t2.goods_id where
t2.goods_id is NOT null
t2.goods_id is null
