mapper.select:
SELECT item_order.oid as orderId, item_order.tradeTime as tradeTime, item_order.tradePay as tradePay, item_order.postPay as postPay, item_order.mainState as mainState, item_order.isTbOrder as isTbOrder, item_order.isTbShipments as isTbShipments, item_order.webSite as webSite, item_order.payTime as payTime, item_order_sub.soid as childOrderId, item_order_sub.goods_id as goodsId, item_order_sub.title as title, item_order_sub.pic_url as imgsrc, item_order_sub.color as color, item_order_sub.size as size, item_order_sub.goods_no as goodsNo, item_order_sub.out_of_stok_reason as haveGoodsTime, CONVERT (item_order_sub.price * 0.01, DECIMAL (10, 2)) as price, item_order_sub.num as num, item_order_sub.out_of_stok as stockoutNum, item_order_sub.in_stok as haveTakeGoodsNum, item_order_refund.refund_id as refundId, if(item_order_refund.type!=5,item_order_refund.number-item_order_refund.fail_number,item_order_refund.fail_number) as afterSaleNum, item_order_refund.type as type, IF (ISNULL(item_order_refund. STATUS),NULL,IF (item_order_refund. STATUS = 2,2,IF (item_order_refund. STATUS = 3, 3,IF(item_order_refund. STATUS = 4,4,1)))) as state FROM ( SELECT item_order.oid oid, item_order.create_time tradeTime, CONVERT (item_order.total_fee * 0.01,DECIMAL (10, 2)) tradePay, CONVERT (item_order_logistics.money * 0.01,DECIMAL (10, 2)) postPay, item_order.ORDER_STATUS mainState, item_order.TYPE = 2 isTbOrder, item_order.tb_send isTbShipments, item_order.WEB_SITE webSite, item_order.pay_time payTime FROM `item_order` item_order INNER JOIN `item_order_logistics` item_order_logistics ON item_order.oid = item_order_logistics.oid AND item_order.USER_ID = #{userId} AND item_order.DISENABLE = 0
AND item_order.OID = #{bo.orderId}
AND item_order.CREATE_TIME >= #{bo.st}
AND item_order.CREATE_TIME <= CONCAT(#{bo.et},' 23:59:59')
AND item_order.order_status = #{bo.status}
AND item_order_logistics.NAME = #{bo.receiver}
AND item_order_logistics.telephone = #{bo.telePhone}
AND item_order.type=2
AND item_order.type!=2
ORDER BY item_order.create_time DESC LIMIT #{startIndex},#{endIndex} ) item_order LEFT JOIN `item_order_sub` item_order_sub ON item_order.oid = item_order_sub.oid
AND item_order_sub.goods_no LIKE CONCAT('%', #{bo.goodsNo}, '%')
LEFT JOIN `item_order_refund` item_order_refund ON item_order_refund.soid = item_order_sub.soid
